import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { User, Shield, GraduationCap, Settings } from 'lucide-react';

const RoleSelection = () => {
  const { user } = useAuth();
  const navigate = useNavigate();

  const handleRoleSelection = (selectedRole) => {
    if (selectedRole === 'student') {
      navigate('/student');
    } else if (selectedRole === 'admin') {
      navigate('/admin');
    }
  };

  if (!user) {
    navigate('/login');
    return null;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-100 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="bg-white rounded-xl shadow-lg p-8">
          {/* Header */}
          <div className="text-center">
            <div className="mx-auto h-12 w-12 bg-purple-600 rounded-full flex items-center justify-center">
              <User className="h-6 w-6 text-white" />
            </div>
            <h2 className="mt-6 text-3xl font-extrabold text-gray-900">
              Welcome, {user.name}!
            </h2>
            <p className="mt-2 text-sm text-gray-600">
              Please select your role to continue
            </p>
          </div>

          {/* Role Cards */}
          <div className="mt-8 space-y-4">
            {user.role === 'student' && (
              <button
                onClick={() => handleRoleSelection('student')}
                className="w-full flex items-center p-4 border-2 border-gray-200 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors duration-200 group"
              >
                <div className="flex-shrink-0">
                  <div className="h-10 w-10 bg-blue-100 rounded-lg flex items-center justify-center group-hover:bg-blue-200">
                    <GraduationCap className="h-5 w-5 text-blue-600" />
                  </div>
                </div>
                <div className="ml-4 text-left">
                  <h3 className="text-lg font-medium text-gray-900">Student Dashboard</h3>
                  <p className="text-sm text-gray-500">
                    View and manage your achievements, participate in activities
                  </p>
                </div>
              </button>
            )}

            {user.role === 'admin' && (
              <button
                onClick={() => handleRoleSelection('admin')}
                className="w-full flex items-center p-4 border-2 border-gray-200 rounded-lg hover:border-green-500 hover:bg-green-50 transition-colors duration-200 group"
              >
                <div className="flex-shrink-0">
                  <div className="h-10 w-10 bg-green-100 rounded-lg flex items-center justify-center group-hover:bg-green-200">
                    <Settings className="h-5 w-5 text-green-600" />
                  </div>
                </div>
                <div className="ml-4 text-left">
                  <h3 className="text-lg font-medium text-gray-900">Admin Dashboard</h3>
                  <p className="text-sm text-gray-500">
                    Manage activities, approve achievements, view analytics
                  </p>
                </div>
              </button>
            )}

            {!user.isApproved && user.role === 'admin' && (
              <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
                <div className="flex">
                  <Shield className="h-5 w-5 text-yellow-400" />
                  <div className="ml-3">
                    <h3 className="text-sm font-medium text-yellow-800">
                      Account Pending Approval
                    </h3>
                    <p className="mt-1 text-sm text-yellow-700">
                      Your admin account is awaiting approval. Please contact the system administrator.
                    </p>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* User Info */}
          <div className="mt-6 pt-6 border-t border-gray-200">
            <div className="text-sm text-gray-600">
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Role:</strong> {user.role}</p>
              {user.department && <p><strong>Department:</strong> {user.department}</p>}
              {user.year && <p><strong>Year:</strong> {user.year}</p>}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RoleSelection;
