import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { User, Shield, GraduationCap, Settings } from 'lucide-react';

const RoleSelection = () => {
  const { user } = useAuth();
  const navigate = useNavigate();

  const handleRoleSelection = (selectedRole) => {
    if (selectedRole === 'student') {
      navigate('/student');
    } else if (selectedRole === 'admin') {
      navigate('/admin');
    } else if (selectedRole === 'superadmin') {
      navigate('/superadmin');
    }
  };

  if (!user) {
    navigate('/login');
    return null;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-purple-50 to-pink-100 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl w-full">
        {/* Animated Background Elements */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute top-20 left-10 w-72 h-72 bg-purple-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
          <div className="absolute top-40 right-10 w-72 h-72 bg-yellow-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>
          <div className="absolute bottom-20 left-20 w-72 h-72 bg-pink-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-4000"></div>
        </div>

        <div className="relative bg-white/80 backdrop-blur-lg rounded-3xl shadow-2xl p-8 border border-white/20">
          {/* Header */}
          <div className="text-center mb-8">
            <div className="mx-auto h-20 w-20 bg-gradient-to-br from-purple-600 to-blue-600 rounded-2xl flex items-center justify-center shadow-lg transform hover:scale-110 transition-transform duration-300">
              <User className="h-10 w-10 text-white" />
            </div>
            <h2 className="mt-6 text-4xl font-extrabold bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent">
              Welcome Back, {user.name}! üéâ
            </h2>
            <p className="mt-3 text-lg text-gray-600">
              Choose your portal to continue your journey
            </p>
          </div>

          {/* Role Cards */}
          <div className="grid md:grid-cols-3 gap-6 mt-10">
            {user.role === 'superadmin' && (
              <button
                onClick={() => handleRoleSelection('superadmin')}
                className="group relative overflow-hidden bg-gradient-to-br from-purple-500 to-purple-700 rounded-2xl p-6 transform hover:scale-105 transition-all duration-300 shadow-xl hover:shadow-2xl"
              >
                <div className="absolute inset-0 bg-white/10 transform -skew-y-6 group-hover:skew-y-0 transition-transform duration-500"></div>
                <div className="relative z-10">
                  <div className="h-14 w-14 bg-white/20 backdrop-blur-sm rounded-xl flex items-center justify-center mb-4 group-hover:bg-white/30 transition-colors">
                    <Shield className="h-7 w-7 text-white" />
                  </div>
                  <h3 className="text-xl font-bold text-white mb-2">Super Admin</h3>
                  <p className="text-purple-100 text-sm">
                    Full system control & user management
                  </p>
                </div>
              </button>
            )}

            {user.role === 'student' && (
              <button
                onClick={() => handleRoleSelection('student')}
                className="group relative overflow-hidden bg-gradient-to-br from-blue-500 to-cyan-600 rounded-2xl p-6 transform hover:scale-105 transition-all duration-300 shadow-xl hover:shadow-2xl"
              >
                <div className="absolute inset-0 bg-white/10 transform -skew-y-6 group-hover:skew-y-0 transition-transform duration-500"></div>
                <div className="relative z-10">
                  <div className="h-14 w-14 bg-white/20 backdrop-blur-sm rounded-xl flex items-center justify-center mb-4 group-hover:bg-white/30 transition-colors">
                    <GraduationCap className="h-7 w-7 text-white" />
                  </div>
                  <h3 className="text-xl font-bold text-white mb-2">Student Portal</h3>
                  <p className="text-blue-100 text-sm">
                    Track achievements & participate in activities
                  </p>
                </div>
              </button>
            )}

            {user.role === 'admin' && (
              <button
                onClick={() => handleRoleSelection('admin')}
                className="group relative overflow-hidden bg-gradient-to-br from-green-500 to-emerald-600 rounded-2xl p-6 transform hover:scale-105 transition-all duration-300 shadow-xl hover:shadow-2xl"
              >
                <div className="absolute inset-0 bg-white/10 transform -skew-y-6 group-hover:skew-y-0 transition-transform duration-500"></div>
                <div className="relative z-10">
                  <div className="h-14 w-14 bg-white/20 backdrop-blur-sm rounded-xl flex items-center justify-center mb-4 group-hover:bg-white/30 transition-colors">
                    <Settings className="h-7 w-7 text-white" />
                  </div>
                  <h3 className="text-xl font-bold text-white mb-2">Admin Portal</h3>
                  <p className="text-green-100 text-sm">
                    Manage activities & approve achievements
                  </p>
                </div>
              </button>
            )}
          </div>

          {!user.isApproved && user.role === 'admin' && (
            <div className="mt-8 bg-gradient-to-r from-yellow-50 to-orange-50 border-2 border-yellow-200 rounded-2xl p-6 shadow-lg">
              <div className="flex items-start">
                <Shield className="h-6 w-6 text-yellow-600 mt-1 flex-shrink-0" />
                <div className="ml-4">
                  <h3 className="text-lg font-bold text-yellow-900">
                    ‚è≥ Pending Approval
                  </h3>
                  <p className="mt-2 text-yellow-800">
                    Your admin account is awaiting approval from the System Administrator. You'll receive access once approved.
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* User Info Card */}
          <div className="mt-8 bg-gradient-to-r from-gray-50 to-gray-100 rounded-2xl p-6 border border-gray-200">
            <h3 className="text-sm font-semibold text-gray-500 uppercase tracking-wide mb-3">Account Details</h3>
            <div className="grid md:grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-gray-600">Email:</span>
                <p className="font-semibold text-gray-900">{user.email}</p>
              </div>
              <div>
                <span className="text-gray-600">Role:</span>
                <p className="font-semibold text-gray-900 capitalize">{user.role}</p>
              </div>
              {user.department && (
                <div>
                  <span className="text-gray-600">Department:</span>
                  <p className="font-semibold text-gray-900">{user.department}</p>
                </div>
              )}
              {user.year && (
                <div>
                  <span className="text-gray-600">Year:</span>
                  <p className="font-semibold text-gray-900">{user.year}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RoleSelection;
